import Link from "next/link";
import "./Menu.scss";
import GlobalStyles from "../../styles/GlobalStyles";
import { useState } from "react";

const Menu = () => {
  const [lightMode, setLightMode] = useState(true);

  const lmToggle = param => {
    if (param) {
      setLightMode(false);
    }
    if (!param) {
      setLightMode(true);
    }
  };

  return (
    <div className="menu">
      <Link href="/">
        <a>Home</a>
      </Link>
      <Link href="/about">
        <a>About</a>
      </Link>
      <Link href="/design">
        <a>Design</a>
      </Link>
      <button onClick={() => lmToggle(lightMode)}>Dark/Light</button>
    </div>
  );
};

export default Menu;
