    <style jsx>
      {`
        :root {
          --bg-color: beige;
          --font-color: black;
        }

        body {
          background-color: var(--bg-color);
          color: var(--font-color);
        }
      `}
      ;
    </style>
  );

  return styles;
}

export default GlobalStyles;
