import { useState } from "react";

const ColorProfiler = () => {
  const [mode, setMode] = useState("light");

  const darkMode = (style.innerHTML = `:root {
    --bg-color: black;
  }`);

  const lightkMode = (style.innerHTML = `:root {
    --bg-color: white;
  }`);

  return lightkMode;
};

export default ColorProfiler;
