import Link from "next/link";
import "./Menu.scss";
import GlobalStyles from "../../styles/GlobalStyles";
import { useState } from "react";

const Menu = () => {
  const [lightMode, setLightMode] = useState(true)(
    <div className="menu">
      <Link href="/">
        <a>Home</a>
      </Link>
      <Link href="/about">
        <a>About</a>
      </Link>
      <Link href="/design">
        <a>Design</a>
      </Link>
      <GlobalStyles props={lightMode} />
    </div>
  );
};

export default Menu;
