import { useState } from "react";

const styleLight = (
  <style jsx>
    {`
      :root {
        --bg-color: beige;
        --font-color: black;
      }

      body {
        background-color: var(--bg-color);
        color: var(--font-color);
      }
    `}
    ;
  </style>
);

const styleDark = (
  <style jsx>
    {`
      :root {
        --bg-color: black;
        --font-color: white;
      }

      body {
        background-color: var(--bg-color);
        color: var(--font-color);
      }
    `}
    ;
  </style>
);

function GlobalStyles({ props }) {
  const [lightMode] = useState(props);

  if (lightMode) {
    return styleLight;
  }
  if (!lightMode) {
    return styleDark;
  }
}

export default GlobalStyles;
