import Link from "next/link";
import "./Menu.scss";
import GlobalStyles from "../../styles/GlobalStyles";
import { useState } from "react";

const Menu = () => {
  const [lightMode, setLightMode] = useState(true);

  let btnTxt = "dark";
  const lmToggle = param => {
    if (param) {
      setLightMode(false);
      btnTxt = "light";
      console.log(lightMode);
    }
    if (!param) {
      setLightMode(true);
      btnTxt = "dark";
      console.log(lightMode);
    }
    this.forceUpdate();
  };

  return (
    <div className="menu">
      <Link href="/">
        <a>Home</a>
      </Link>
      <Link href="/about">
        <a>About</a>
      </Link>
      <Link href="/design">
        <a>Design</a>
      </Link>
      <button onClick={() => lmToggle(lightMode)}>{btnTxt}</button>
      <GlobalStyles props={lightMode} />
    </div>
  );
};

export default Menu;
